/**
 * protonThemes Custom Fields JavaScript
 *
 * All JavaScript logic for fields in the post meta box.
 * @since 2.2.3
 *
 */


protonFieldMethods.color = {
	 	_init: function(element){
			if(element.find( '.proton_input_color:not(.wp-color-picker)' ).length){
			var myOptions = {
			    // you can declare a default color here,
			    // or in the data-default-color attribute on the input
			    defaultColor: false,
			    // a callback to fire whenever the color changes to a valid color
			    change: function(event, ui){},
			    // a callback to fire when the input is emptied or an invalid color
			    clear: function() {},
			    // hide the color picker controls on load
			    hide: true,
			    // show a group of common colors beneath the square
			    // or, supply an array of colors to customize further
			    palettes: true
			};
			element.find( '.proton_input_color:not(.wp-color-picker)' ).wpColorPicker(myOptions);
			}
		},
		_destroy: function(element){
		}
 	};

(function ($) {
/**
 * setup_colourpickers()
 *
 * @since 2.2.3
 */

	$(document).ready(function () {
		$( '.proton-field-type-color' ).each(function() {
			protonFieldMethods.color._init($(this));
 		});

	});

})(jQuery);