(function ($) {

 	
/**
 * setup_datepickers()
 *
 * @since 2.2.3
 */
 
 	function setup_datepickers() {
 		if ( $( '.proton_input_daterange' ).length ) {
	 		$( '.proton_input_daterange' ).each(function () {
	 			//var buttonImageURL = $( this ).parent().find( 'input[name=datepicker-image]' ).val();
	 			//var buttonImageURL = $( this ).parent().find( 'input[name=datepicker-image]' ).val();
	 			//$( this ).next( 'input[name=datepicker-image]' ).remove();
	 			//$( '#' + $( this ).attr( 'id' ) ).datepicker( { showOn: 'button', buttonImage: buttonImageURL, buttonImageOnly: true, showAnim: 'slideDown', dateFormat: 'yy-mm-dd' } );});
			
				$( '#' + $( this ).attr( 'id' ) ).datepicker( {  showAnim: 'slideDown', dateFormat: 'dd.mm.yy' } );
				$( this ).next().click(function() {
					$( '#' + $( this ).prev().attr( 'id' ) ).datepicker("show");
				});
			});
			
		}
 	} // End setup_datepickers()
 	

  
 // End protonthemesCustomFields Object // Don't remove this, or the sky will fall on your head.
  
  

/**
 * Execute the above methods in the protonthemesCustomFields object.
 *
 * @since 2.2.3
 */
	$(document).ready(function () {
	
		setup_datepickers();
		
	});
	
})(jQuery);	
	
