/**
 * protonThemes Custom Fields JavaScript
 *
 * All JavaScript logic for fields in the post meta box.
 * @since 2.2.3
 *
 */
(function ($) {
/**
 * setup_wordcounters()
 *
 * @since 2.2.3
 */
 	protonFieldMethods.iconpicker = {
	 	_init: function(element){
			element.find('.proton_input_iconpicker').fontIconPicker({
				source: _Proton__Field_iconpicker.fontawesome,
			    theme: 'fip-darkgrey',
				hasSearch: false
			    //iconsPerPage: 0
			});
		},
		_destroy: function(element){
			if(element.find('.icons-selector').length){
				element.find( '.proton_input_iconpicker' ).fontIconPicker().destroyPicker();
			}
		}
 	};

	$(document).ready(function () {

		if ( $( '.proton-field-type-iconpicker' ).length ) {
			/*$('.proton_input_iconpicker').fontIconPicker({
			    theme: 'fip-darkgrey'
			    //iconsPerPage: 0
			});*/
			protonFieldMethods.iconpicker._init($( '.proton-field-type-iconpicker' ));
		}

	});

})(jQuery);