/**
 * protonThemes Custom Fields JavaScript
 *
 * All JavaScript logic for fields in the post meta box.
 * @since 2.2.3
 *
 */


protonFieldMethods.radioimg = {
	 	_init: function(element){
			if ( jQuery( '.proton-field-radioimg-img' ).length ) {
		 		jQuery( '.proton-field-radioimg-img' ).click( function() {

					jQuery( this ).parent().parent().find( '.proton-field-radioimg-img' ).removeClass( 'proton-field-radioimg-selected' );
					jQuery( this ).parent().parent().find( '.proton-field-radioimg-img' ).removeClass( 'proton-field-radioimg-selected' );
					jQuery( this ).addClass( 'proton-field-radioimg-selected' ).addClass( 'proton-field-radioimg-selected' );

				});
				jQuery( '.proton-field-radioimg-label, .proton-field-radioimg-radio' ).hide();
				jQuery( '.proton-field-radioimg-img' ).show();
			}
		},
		_destroy: function(element){
		}
 	};

(function ($) {


/**
 * setup_chosen_select()
 *
 * @since 2.2.3
 */
 	$(document).ready(function () {

 		protonFieldMethods.radioimg._init('pfc');


	});

})(jQuery);
