/**
 * protonThemes Custom Fields JavaScript
 *
 * All JavaScript logic for fields in the post meta box.
 * @since 2.2.3
 *
 */
 	protonFieldMethods.slider = {
	 	_init: function(element){
			element.find( '.proton_input_slider' ).ionRangeSlider();
		},
		_destroy: function(element){
			if(element.find( '.proton_input_slider' ).hasClass('irs-hidden-input')){
			var slider = element.find( '.proton_input_slider' ).data("ionRangeSlider");
			slider.destroy();
			}
		}
 	};
(function ($) {

/**
 * Execute the above methods in the protonthemesCustomFields object.
 *
 * @since 2.2.3
 */
		$(document).ready(function () {
			$( '.proton-field-type-slider' ).each(function(){
		 		protonFieldMethods.slider._init($(this));
	 		});
		});



})(jQuery);

